import{_ as e,c as t,o as r,ag as l}from"./chunks/framework.DWdSXOaE.js";const h=JSON.parse('{"title":"消费需要有安全感","description":"","frontmatter":{},"headers":[],"relativePath":"奇思妙想/消费需要有安全感.md","filePath":"奇思妙想/消费需要有安全感.md","lastUpdated":1743494601000}'),o={name:"奇思妙想/消费需要有安全感.md"};function i(_,a,n,s,d,p){return r(),t("div",null,[...a[0]||(a[0]=[l('<h1 id="消费需要有安全感" tabindex="-1">消费需要有安全感 <a class="header-anchor" href="#消费需要有安全感" aria-label="Permalink to &quot;消费需要有安全感&quot;">​</a></h1><h2 id="报复性消费为何没有出现" tabindex="-1">报复性消费为何没有出现 <a class="header-anchor" href="#报复性消费为何没有出现" aria-label="Permalink to &quot;报复性消费为何没有出现&quot;">​</a></h2><p>疫情后经济学家预测会出现报复性消费，但是实际效果却没有想象中的全面爆发的现象。经过我的观察大概率是人们对自己的收入预期有变化的原因。</p><p>人们看到周围的商户一家家倒闭之后不可能会认为自己的生活会越来越好，所谓的寒风会吹向每个人就是这样。</p><p>当人们的收入预期是下降，同时生活福利没有变化或者下降，那么他们就会认为自己的生活会变差，因此会变得更加理性，报复性消费就没有出现。</p><h2 id="消费需要有安全感-1" tabindex="-1">消费需要有安全感 <a class="header-anchor" href="#消费需要有安全感-1" aria-label="Permalink to &quot;消费需要有安全感&quot;">​</a></h2><p>我认为人放心的消费需要有如下几个安全感:</p><ol><li>消费不会影响未来的生活，收入有增长预期</li><li>买到的产品是安全的，消费买到的产品是有监管和测试的</li><li>就算没有钱也会有社会福利兜底的保障</li><li>售后保障，商品和食物消费后可以售后，或者有售后保障的保障</li></ol>',8)])])}const m=e(o,[["render",i]]);export{h as __pageData,m as default};
