import{_ as t,c as e,o as r,ag as s}from"./chunks/framework.8SQNO8WI.js";const o="/assets/2020-03-08-23-43-50.B2VhpQj4.jpg",i="/assets/2020-03-08-23-44-40.COgSzm-K.jpg",p="/assets/2020-03-08-23-46-09.BhGu2bUg.jpg",h="/assets/2020-03-08-23-46-53.C4HZ5pZ4.jpg",l="/assets/2020-03-08-23-47-38.DqxvKKFN.jpg",n="/assets/2020-03-08-23-48-27.yWT1cI2r.jpg",c="/assets/2020-03-08-23-49-52.pbTumaAg.jpg",d="/assets/2020-03-08-23-50-16.rYZ-WFSj.jpg",x=JSON.parse('{"title":"少儿编程上手和评价","description":"","frontmatter":{"title":"少儿编程上手和评价","tags":["少儿编程","Ucode"],"categories":[["少儿编程","Ucode"],["想法","杂谈"]],"abbrlink":"e60","date":"2020-03-09T00:05:12.000Z"},"headers":[],"relativePath":"生活记录/少儿编程/少儿编程.md","filePath":"生活记录/少儿编程/少儿编程.md","lastUpdated":1726751882000}'),_={name:"生活记录/少儿编程/少儿编程.md"};function m(u,a,b,g,f,q){return r(),e("div",null,[...a[0]||(a[0]=[s('<h1 id="少儿编程上手和评价" tabindex="-1">少儿编程上手和评价 <a class="header-anchor" href="#少儿编程上手和评价" aria-label="Permalink to &quot;少儿编程上手和评价&quot;">​</a></h1><p>今天上手体验了一款少儿编程产品，他的名字是Ucode是一款比较小众的少儿编程工具，经过几个小时时间的开发体验，我写下这篇文章做记录。</p><p>这是Ucode的<a href="https://ubtedu.github.io/uCodeWiki/zh/guide/" target="_blank" rel="noreferrer">官方文档</a>这个网上的资料很少，但是只要有一点编程基础就可以轻松上手这个小玩具。</p><p>我设计了一款小游戏来体验这个开发：小明是个冒险家，他喜欢冒险，但是由于当前疫情严重所以它在第一关的时候就遇到了拦路的北极熊，他回答出问题才能通过这一关，完成之后玩家可以回家庆祝，也可以选择重新开始游戏。</p><h2 id="实现部分" tabindex="-1">实现部分 <a class="header-anchor" href="#实现部分" aria-label="Permalink to &quot;实现部分&quot;">​</a></h2><p>贴出代码截图</p><h3 id="角色代码" tabindex="-1">角色代码 <a class="header-anchor" href="#角色代码" aria-label="Permalink to &quot;角色代码&quot;">​</a></h3><p><img src="'+o+'" alt=""></p><h3 id="北极熊" tabindex="-1">北极熊 <a class="header-anchor" href="#北极熊" aria-label="Permalink to &quot;北极熊&quot;">​</a></h3><p><img src="'+i+'" alt=""></p><h3 id="三个字母" tabindex="-1">三个字母 <a class="header-anchor" href="#三个字母" aria-label="Permalink to &quot;三个字母&quot;">​</a></h3><p><img src="'+p+'" alt=""></p><h3 id="重新玩的按钮" tabindex="-1">重新玩的按钮 <a class="header-anchor" href="#重新玩的按钮" aria-label="Permalink to &quot;重新玩的按钮&quot;">​</a></h3><p><img src="'+h+'" alt=""></p><h2 id="游戏截图" tabindex="-1">游戏截图 <a class="header-anchor" href="#游戏截图" aria-label="Permalink to &quot;游戏截图&quot;">​</a></h2><p>开始</p><p><img src="'+l+'" alt=""></p><p>答题</p><p><img src="'+n+'" alt=""></p><p>回到家庆祝</p><p><img src="'+c+'" alt=""></p><p>结束界面，END</p><p><img src="'+d+'" alt=""></p><h2 id="github下载工程文件" tabindex="-1">Github下载工程文件 <a class="header-anchor" href="#github下载工程文件" aria-label="Permalink to &quot;Github下载工程文件&quot;">​</a></h2><p><a href="https://github.com/whp98/Ucode_Demo" target="_blank" rel="noreferrer">工程文件</a></p><h2 id="评价" tabindex="-1">评价 <a class="header-anchor" href="#评价" aria-label="Permalink to &quot;评价&quot;">​</a></h2><p>我觉得少儿编程的小玩意虽然和软件开发没啥关系但是其中的一些高级功能简单易用可以更加的开启儿童的编程思想，确实是不错的产品，尤其是其中还有一部分AI的模块，其简单易用的特点可以让没有语言基础的少儿上手更加简单的可视化编程，并且思想可以直接上升到更加抽象的层面，从而更加清晰的了解程序运行机理，这是很不错的。</p><p>一些不足也是可以看出来的，首先这个程序代码没有啥可以移植的方式，这让少儿编程只能停留在玩具层面，这个编程虽然简单但是真实开发环境中比这个复杂得多，而且我发现少儿编程的网上可以参考的内容也比较少，甚至官方文档不是很齐全，这都是少儿编程需要进步的方向，总体来说我是很看好这一个行业的。</p>',28)])])}const P=t(_,[["render",m]]);export{x as __pageData,P as default};
